// 1. –û—á–∏—Å—Ç–∫–∞ —Å—Ç–∞—Ä–æ–≥–æ –ª–æ–±–±–∏ (–µ—Å–ª–∏ –µ—Å—Ç—å), —á—Ç–æ–±—ã –Ω–µ –¥—É–±–ª–∏—Ä–æ–≤–∞—Ç—å
let oldLobby = document.getElementById('dreamora_lobby');
if (oldLobby) oldLobby.remove();

// 2. –°–æ–∑–¥–∞–µ–º –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä-–æ–≤–µ—Ä–ª–µ–π
let lobby = document.createElement('div');
lobby.id = 'dreamora_lobby';
// –°—Ç–∏–ª–∏: —á–µ—Ä–Ω—ã–π —Ñ–æ–Ω, –Ω–∞ –≤–µ—Å—å —ç–∫—Ä–∞–Ω, –ø–æ–≤–µ—Ä—Ö –≤—Å–µ–≥–æ (z-index: 9999)
lobby.style = "position:fixed; top:0; left:0; width:100%; height:100%; background:#1a1a1a; z-index:9999; overflow-y:auto; color:white; font-family:sans-serif; padding:20px; box-sizing:border-box;";

// 3. –î–∞–Ω–Ω—ã–µ —Ç–≤–æ–∏—Ö –Ω–æ–≤–µ–ª–ª (—É–±–µ–¥–∏—Å—å, —á—Ç–æ –ø—É—Ç–∏ –∫ –∫–∞—Ä—Ç–∏–Ω–∫–∞–º –∏ —Ñ–∞–π–ª–∞–º –≤–µ—Ä–Ω—ã–µ!)
window.myNovels = [
    {
        id: "elina_robot",
        title: "–ü—Ä–æ—Ç–æ–∫–æ–ª –≠–ª–∏–Ω–∞",
        desc: "–¶–∏—Ñ—Ä–æ–≤–æ–µ —Å–æ–∑–Ω–∞–Ω–∏–µ –≤ —Ç–µ–ª–µ —Ä–æ–±–æ—Ç–∞...",
        genre: "Sci-Fi",
        img: "images/preview/elina.jpg",
        file: "stories/elina/ep1.json" // –í—Å–µ–≥–¥–∞ Ep 1 –¥–ª—è –Ω–æ–≤–∏—á–∫–æ–≤
    },
    {
        id: "elevator_story",
        title: "–ó–∞—Å—Ç—Ä—è–≤—à–∏–µ",
        desc: "–ú–∞—Ä–∫ –∏ –ê—Ä—Ç—É—Ä –∑–∞—Å—Ç—Ä—è–ª–∏ –≤ –ª–∏—Ñ—Ç–µ...",
        genre: "–î—Ä–∞–º–∞ / –¢—Ä–∏–ª–ª–µ—Ä",
        img: "images/preview/elevator.jpg",
        file: "stories/elevator/ep1.json"
    }
];

window.launchNovel = function(novelId, storyFile) {
    console.log("üõ†Ô∏è –ü—Ä–æ—Ç–æ–∫–æ–ª –∑–∞–ø—É—Å–∫–∞ –∞–∫—Ç–∏–≤–∏—Ä–æ–≤–∞–Ω. –ó–∞–≥—Ä—É–∑–∫–∞ —Ñ–∞–π–ª–∞:", storyFile);

    const lobby = document.getElementById('dreamora_lobby');
    if (lobby) lobby.style.display = 'none';

    const onScriptLoaded = function() {
        console.log("‚úÖ –ú–æ–¥—É–ª—å " + novelId + " –∑–∞–≥—Ä—É–∂–µ–Ω. –î–≤–∏–∂–æ–∫ –ø–µ—Ä–µ—Ö–≤–∞—Ç–∏–ª —É–ø—Ä–∞–≤–ª–µ–Ω–∏–µ.");

        // –£–¥–∞–ª—è–µ–º —Å–ª—É—à–∞—Ç–µ–ª—å —Å—Ä–∞–∑—É –ø–æ—Å–ª–µ —Å—Ä–∞–±–∞—Ç—ã–≤–∞–Ω–∏—è
        tuesday.removeEventListener('script_loaded', onScriptLoaded);
    };

    // –°–ª—É—à–∞–µ–º —Å–æ–±—ã—Ç–∏–µ –≥–æ—Ç–æ–≤–Ω–æ—Å—Ç–∏
    tuesday.addEventListener('script_loaded', onScriptLoaded);

    try {
        // –ó–∞–≥—Ä—É–∑–∫–∞ —Ñ–∞–π–ª–∞ –∏ –∞–≤—Ç–æ–∑–∞–ø—É—Å–∫ –ø–µ—Ä–≤–æ–π —Å—Ü–µ–Ω—ã
        load_story("file", storyFile);
    } catch (e) {
        console.error("üöÄ –ö—Ä–∏—Ç–∏—á–µ—Å–∫–∏–π —Å–±–æ–π –ø—Ä–∏ –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏–∏ —Ñ–∞–π–ª–∞ –∏—Å—Ç–æ—Ä–∏–∏:", e);
        tuesday.removeEventListener('script_loaded', onScriptLoaded);

        // –ù–∞ —Å–ª—É—á–∞–π —Å–±–æ—è ‚Äî –≤–æ–∑–≤—Ä–∞—â–∞–µ–º –ª–æ–±–±–∏, —á—Ç–æ–±—ã —é–∑–µ—Ä –Ω–µ –∑–∞—Å—Ç—Ä—è–ª –≤ –ø—É—Å—Ç–æ—Ç–µ
        if (lobby) lobby.style.display = 'flex';
    }
};

// 5. –ì–µ–Ω–µ—Ä–∞—Ü–∏—è —Ç–∞–±–ª–∏—Ü—ã (Grid)
let gridHTML = `<h1 style="text-align:center; color:#00d4ff;">Dreamora Games</h1>`;
gridHTML += `<div style="display:grid; grid-template-columns: repeat(auto-fill, minmax(250px, 1fr)); gap:30px; margin-top:30px;">`;

window.myNovels.forEach(novel => {
    gridHTML += `
        <div class="novel-card" onclick="launchNovel('${novel.id}', '${novel.file}')" style="cursor:pointer; text-align:center;">
            <img src="${novel.img}" style="width:100%; border-radius:15px; border:2px solid #333; transition:0.3s;" 
                 onmouseover="this.style.borderColor='#00d4ff'" onmouseout="this.style.borderColor='#333'">
            <div style="font-size:1.4em; font-weight:bold; margin:10px 0;">${novel.title}</div>
            <div style="font-size:0.9em; color:#aaa; height:40px; overflow:hidden;">${novel.desc}</div>
            <div style="font-size:0.8em; color:#00d4ff; margin-top:5px; text-transform:uppercase;">${novel.genre}</div>
        </div>
    `;
});

gridHTML += `</div>`;
lobby.innerHTML = gridHTML;

// 6. –î–æ–±–∞–≤–ª—è–µ–º –Ω–∞ —Å—Ç—Ä–∞–Ω–∏—Ü—É
document.body.appendChild(lobby);

document.addEventListener('pointerdown', function(event) {
    // 1. –ù–µ —Å—Ä–∞–±–∞—Ç—ã–≤–∞–µ—Ç, –µ—Å–ª–∏ –æ—Ç–∫—Ä—ã—Ç–æ –ª–æ–±–±–∏
    const lobby = document.getElementById('dreamora_lobby');
    if (lobby && lobby.style.display !== 'none') return;

    // 2. –ù–µ —Å—Ä–∞–±–∞—Ç—ã–≤–∞–µ—Ç, –µ—Å–ª–∏ –∫–ª–∏–∫–Ω—É–ª–∏ –ø–æ –∫–Ω–æ–ø–∫–∞–º (—Å—Ç—Ä–µ–ª–∫–∏ < > –∏–ª–∏ –∫–Ω–æ–ø–∫–∏ –≤—ã–±–æ—Ä–∞)
    if (event.target.closest('.tue_controll') || event.target.closest('.tue_choice')) return;

    // 3. –í—ã–∑—ã–≤–∞–µ–º –≤—Å—Ç—Ä–æ–µ–Ω–Ω—É—é —Ñ—É–Ω–∫—Ü–∏—é –ø–µ—Ä–µ—Ö–æ–¥–∞
    // –í —Ç–≤–æ–µ–º index.html –æ–Ω–∞ —Å–∞–º–∞ –∑–Ω–∞–µ—Ç: –¥–æ–ø–µ—á–∞—Ç–∞—Ç—å —Ç–µ–∫—Å—Ç –∏–ª–∏ —Å–º–µ–Ω–∏—Ç—å —Å—Ü–µ–Ω—É
    if (typeof go_story === 'function') {
        go_story();
    }
});
